REPLACE INTO `cscart_product_descriptions` VALUES (243,'ja','Apple iPod shuffle','','','<p>iPod shuffleは美しく、身につけやすい音楽プレーヤーです。クリックできる大きなコントロールパッド搭載で操作もらくらく。/p>','','','','','','<p class=\"product-promo-text\"><span class=\"product-promo-header\">5000円以上のお買い上げで送料無料!</span><span class=\"product-promo-body\">本日より2日以内のご注文は来週月曜日の発送となります</span></p>');
